import { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import styled from "styled-components";

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
`;

const Input = styled.input`
  padding: 10px;
  margin-bottom: 15px;
  border: 1px solid #ccc;
  border-radius: 5px;
  width: 300px;
  font-size: 16px;
`;
const Botao = styled.button`
  padding: 10px 20px;
  background-color: #0077cc;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  font-size: 16px;

  &:hover {
    background-color: #005fa3;
  }
`;

function Home() {
  const location = useLocation();
  const nick = location.state?.nick;
  const [mensagem, setAtualizaMensagem] = useState("");
  const [lista, setLista] = useState([]);
  const [socket, setSocket] = useState(null);

  const infoBot = [
    "ðŸ›’ Visite a nossa loja oficial: https://www.furia.gg/",
    "ðŸ“± Siga a FURIA nas redes sociais! Instagram: @furiagg",
  ];

  useEffect(() => {
    const newSocket = new WebSocket("ws://localhost:8000");
    setSocket(newSocket);
    newSocket.onmessage = (event) => {
      setLista((prevListas) => [...prevListas, event.data]);
    };
    return () => newSocket.close();
  }, []);

  function mandarMensagem() {
    if (mensagem.trim() !== "") {
      socket.send(mensagem);
      setAtualizaMensagem("");
    }
  }

  function atualizar(event) {
    setAtualizaMensagem(event.target.value);
  }

  return (
    <Container>
      <div>
        <h1>SEJA BEM VINDO AO CHAT DA FURIA, {nick}!</h1>
        <div>
          {lista.map((msg, index) => (
            <div key={index}>{msg}</div>
          ))}
        </div>
        <div>
          <Input
            type="text"
            value={mensagem}
            onChange={atualizar}
            placeholder="Mensagem"
          />
          <Botao onClick={mandarMensagem}>Enviar</Botao>
        </div>
      </div>
    </Container>
  );
}

export default Home;
